css:
@-moz-document domain("d3.ru") {
.b-gertruda { 
	opacity:0.1;
    transition:0.5s;
}
.b-gertruda:hover {
	opacity:1;
}
.b-user_ad_container, .b-fixed_sidebar, .b-top_horizontal_banner {
    display:none;
}
body, .l-subdomain_post_custom_design, .l-subdomain_post_custom_design .b-elections_candidate_info, .l-subdomain_post_custom_design, .l-footer {
    background-color: #222;
    color: #bbb;
}
.b-index_posts_holder {
	padding-right: 0px;
}
.post_body img, .b-post_cover img {width: 200px;opacity:0.7;transition:0.3s;}
.post_body img:hover, .b-post_cover img:hover {width: 100%;opacity:1;}
h3 a {font-size:14px;}
div.post_body_text {font-size:12px;color: #999;}
}
