css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://bato.to/reader"), url-prefix("http://bato.to/read") {
  html, body, #comic_wrap, #content, #mainContent, #ipbwrapper { 
    width: auto; 
    background-color: white !important; 
    max-width: none; 
    min-width: none; 
    min-height: none; 
    max-height: none;
    padding: 0;
    margin: 0;
  }
  
  #branding, #user_bar, #footer_utilities, .moderation_bar { display: none; }
  #ipboard_body { background: none; }
  
  #comic_wrap > div {
    top: auto !important;
    right: auto !important;
  }
  
  #comic_page {
    height: 100vh;
  }
}
