css:
@-moz-document domain("realclearpolitics.com") {

body, p, div.entry-body {font-family: Verdana !important}

body, body.article.rcp, div#container {background-color: green }

div.site-logo, div.beta-wrapper, div.alpha-wrapper {background-color: #2DC12D }

div.alpha {background-color: #e0cfa0 !important}

div#beta {background-color: gray}

div#masthead {background-color: lightgray}

div.up-next, div.up-next a, span.title { color: white !important }
}
