css:
@-moz-document domain("songza.com") {
.subnav, *[class^="browse-"], *[class^="static-"] {
    background-color: #001639 !important;
}

*[class^="browse-"], *[class^="fullplayer-"], .settings *, *[class$="-byline"] a {
    color: rgba(255, 255, 255, 0.8);
}

fieldset input[type="text"], input[type="password"], textarea {
    background-color: #060039 !important;
    border: 1px solid rgba(255, 255, 255, 0.8) !important;
}

h1, h2, h3, h4, h5, h6 {
    color: rgba(255, 255, 255, 0.8) !important;
}

.fullplayer-info { background: #00275f !important; }
}
