css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ruby-doc.org") {
  .class #documentation, .file #documentation, .module #documentation {
    max-width: none;
  }
  .wrapper {
    width: 100%;
    max-width: 1400px;
  }
  .grids {
    max-width: 1400px;
    padding: 0 0.5em;
  }
  .class #metadata, .file #metadata, .module #metadata {
    width: auto;
    min-width: 260px;
  }
  #documentation .method-detail {
    padding: 0.5em 1em;
    cursor: pointer;
    margin: 0.5em 0 0.5em 1.5em;
  }
  #documentation .method-heading {
    background-position: left 80%;
  }
  pre.ruby {
    max-width: 560px;
  }
  pre {
    margin: 1em 0px;
    padding: 1em;
  }
  #disqus_thread {
    max-width: 1010px;
    margin: 0px auto;
  }
  .adzbox, .adzbox-index {
    display: none;
  }
}
