css:
@-moz-document domain("youtube.com") {
  #masthead-positioner {
      position:absolute !important;
  }

  #yt-masthead-container, #masthead-appbar, .guide-pinned.show-guide #appbar-guide-menu {
      background:#f1f1f1 !important;
      border-color: #f1f1f1 !important;
  }
  .guide-pinned.show-guide #appbar-guide-menu {
      position:absolute;
      overflow:visible !important;
  }
  .show-guide:not(.guide-pinned) #masthead-positioner {
      position: fixed !important;
  }
}
