css:
@-moz-document url-prefix("https://docs.google.com/document/d/") {
/* Background image */
body,
#kix-appview
{
    background-color: transparent !important;
    background-image: url('https://apprecap.files.wordpress.com/2011/04/screen-shot-2011-04-08-at-10-49-50-pm.png') !important;
    background-size: cover !important;
    border: 0 !important;
    outline: 0 none !important;
}

/* Sheet background */
.kix-page.kix-page-compact-first
{
    background-color: transparent !important;
}
.kix-page-paginated,
.kix-paginateddocumentplugin.kix-paginateddocumentplugin-compact-mode
{
    border: none !important;
    box-shadow: 0 0 0 0 !important;
}
.kix-page.kix-page-compact,
.kix-page.kix-page-paginated,
.kix-page-content-wrapper,
.docs-ui-unprintable
{
    background-color: transparent !important;
}

/* Editor resize */
/*.kix-appview-editor
{
  height: 400px !important;
}*/

/* ---- beginning of auto-hide toolbar and other eleme ---- */

/* Comment section */
.kix-commentoverlayrenderer-normal
{
    opacity: .1 !important;
}
.kix-commentoverlayrenderer-highlighted
{
    background-color: #ffe168 !important;
    opacity: .4 !important;
}
.docos-anchoreddocoview
{
    opacity: .1 !important;
}
.docos-anchoreddocoview:hover,
.docos-docoview-active.docos-anchoreddocoview:hover,
.docos-docoview-active.docos-anchoreddocoview
{
    opacity: .9 !important;
}

div.docs-ui-unprintable:last-child
{
    background-color: transparent !important;
}
#docs-chrome
{
    width: 100% !important;
    z-index: 100; 
    position: fixed !important;
    margin-top: 0px !important;
    -moz-transition: all 0.8s;     
    -webkit-transition: all 0.8s;
    opacity: 0.0 !important;
}

#docs-chrome:hover
{
    margin-top: 0 !important;
    opacity: 1 !important;
    -moz-transition: all 0.4s;     
    -webkit-transition: all 0.4s;
}

::-webkit-scrollbar-thumb
{
     -webkit-transition: background-color 0.3s;
}

iframe
{
     border-top: none !important;
}

/* ---- end of auto-hide toolbar ---- */



}
