css:
@-moz-document domain("yammer.com") {
div.yj-selector-right-sidebar, div#column-one {
    display: none;
    height: 0;
    padding: 0;
    width: 0;
    overflow: auto;
}

div.yk-center-block, div#main-column {
    margin: 0;
    max-width: 100%;
}

div#column-two {
    margin-left: 0;
    min-width: 0;
}

div.yj-next-group-cta {
    height: 0;
    padding: 0;
}

div.yj-chatmanager {
    bottom: 0;
    margin-bottom: 0;
}

}
