css:
@-moz-document domain("youmightnotneedjquery.com") {
header.search {
	position: fixed;
	top: 0;
	z-index: 999;
	background-color: #FFF;
	width: 100%;
	max-width: 100%;
	margin-top: 0;
	background-color: #414141;
	color: white;
}

header.search > input[type="search"] {
	margin: 10px 0;
	height: 10px;
	width: 50%;
	font-size: 14pt;
	float: left;
	background-color: #d1d1d1;
	color: white;
}

header.search > input[type="search"]:focus {
	background: #EEE;
	color: #414141;
}

header.search > .field {
	display: block;
	float: right;
	width: 48%;
	top: 10px;
}

header.search > .field > label[for="version"] {
	display: block;
	position: relative;
	padding: 0;
	width: 49%;
	font-size: 10pt;
	float: left;
}

header.search > .field > .slider {
	display: block;
	float: right;
	position: relative;
	height: 20px;
	width: 49%;
}

header.search > .field > .slider > input[type='range'] {
	height: 15px;
}
}
