@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("feedly.com") {
    [data-title^="Pr:"],
    [data-title^="PR:"],
    [data-title^="Pr："],
    [data-title^="PR："],
    [data-title^="AD:"],
    [data-title^="Ad:"]
    {
    
      display: none !important;
    }
    .title
    {
        font-size: 1em !important;
        line-height: 3em !important;
        margin: 0px !important;
    }
    .entryBody,
    h3
    {
        font-size: 1em !important;
        line-height: 1.5em !important;
    }
}
