css:
/*
	==== MyAnimeList.net - Nav Restyle by Zealotus ====
	Some ideas taken from other styles. ;)
*/
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){*{/*$*/}
	#contentWrapper {
		margin-top:					25px !important;
	}
	#menu {
		position:					fixed !important;
			top:					0 !important;
		z-index:					1000 !important;
	}
	#menu, #menu_left, #menu_left #nav>li {
		background:					transparent !important;
	}
	#menu_left, #menu_right {
		opacity:					.2 !important;
	}
	#menu_left {
		width: auto !important;
	}
	#menu_left:hover, #menu_right:hover {
		opacity:					1 !important;
	}
	#menu_right:hover {
		box-shadow:			2px 2px 8px #666;
	}
	#menu_right {
		background:					#458 !important;
		border-radius:			2em/4em;
		height:						25px !important;
	}
	#menu_right #searchBar {
		padding-top:				1px !important;
	}
	#menu_left #nav>li {
		margin-right:				4px !important;
	}
	#menu_left #nav>li>a {
		border-radius:			2em/4em;
		padding:					3px 3px 0px !important;
		height:						22px !important;
	}
	#menu_left #nav>li>a:hover {
		box-shadow:			1px 1px 8px #666;
	}
	#menu_left #nav>li>ul {
		border-radius:			2em/4em;
	}
	#menu_left #nav>li>ul>li {
		background:					transparent !important;
	}
	#menu_left #nav:hover>li>ul>li>a {
		box-shadow:			1px 1px 6px #666;
	}
	#menu_left #nav>li>ul>li>a {
		border-radius:			2em/4em;
		margin-top:					1px !important;
		padding:					1px 5px !important;
		z-index:					1001 !important;
	}
	#menu_left #nav>li>ul>li>span a {
		background:					transparent !important;
		border-radius:			2em/4em;
		margin-top:					1px !important;
		padding:					1px 5px !important;
		z-index:					1001 !important;
	}
	#menu_left #nav>li>ul>li>span a:hover {
		/* background:					#458 !important; */
	}
}
