css:
@-moz-document url-prefix("http://atelier801.com/") {
    body {background: url('http://www.transformice.com/wp-content/themes/Themice/images/fond.jpg') top repeat-x #1e1915;}
    .cadre-type-auteur-joueur{ color: #DDB290; text-shadow: none; }
    .cadre-sujet-titre-sombre{ color: #B0B0C0; margin-left: 3px; margin-right: 3px;  }
    .ligne-section-actu{ background-color: rgba(100, 81, 66, 1); border: 1px #887261 dashed; }
    .table-section{ width: 100%; background-color: #6F5D4F; border: 1px #887261 dashed; }
    .cadre-forum{ background-color: rgba(60, 46, 29, 0.41); padding-top: 5px; margin-bottom: 10px; height: 100%; }
    .cadre-sujet-date{ font-size: 14px; color: #97A2EA; }
    .footer{ background-color: #594C41; color: #B0A8A0; border-top: 3px #383029 dashed; }
    .cadre-sujet{ background-color: rgba(60, 35, 22, 0.76);vertical-align:middle;margin-bottom:10px; }
    .cadre-relief{ border: 3px #745F4F dashed; }
    .cadre-message{ background-color: rgba(63, 79, 16, 0.69); margin-bottom: 10px; padding-bottom: 5px; padding-left: 10px; overflow: visible; border: 3px #3A490F dashed; }
    .cadre-repondre{padding-top:10px;background-color: rgba(63, 79, 16, 0.46);overflow-x:auto;border: 0px;border: 3px #21270C dashed;}
    .lien-blanc{ color: #E7E7ED; text-decoration: none; }
}
