css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document 
domain("thepiratebay.org"),
domain("thepiratebay.asia"),
domain("thepiratebay.ac"),
domain("thepiratebay.ae"),
domain("thepiratebay.se"),
domain("thepiratebay.la"),
domain("thepiratebay.mn"),
domain("thepiratebay.gd"),
domain("thepiratebay.gs"),
domain("thepiratebay.am"),
domain("thepiratebay.vg"),
domain("thepiratebay.sx"),
domain("thepiratebay.pe"),
domain("openpirate.org"),
domain("unblockpirate.org")
{

body, #header, h2, td, #details, .nfo,
#title, .comment, #filelistContainer,
#CommentDiv
{
background: #000000 !important;
}
input, select, textarea, th
{
background: #2051A0 !important;
}
#filelistContainer, .nfo
{
border: 1px #2051A0 solid !important;
}
.byline
{
border-top: 1px #FFFFFF solid !important;
}
.vertTh
{
border-bottom: 1px #FFFFFF solid !important;
}
h2
{
border-bottom: none !important;
}
input, select, textarea
{
border: 1px #FFFFFF solid !important;
}
body, #footer, dt, p, textarea,
h1, h2, h3, h4, h5, h6,
label, input, select, .detDesc, .byline
{
color: #FFFFFF !important;
}
a
{
color: #FFFFFF !important;
text-decoration: none !important;
}
a:hover
{
color: #FFFFFF !important;
text-decoration: underline !important;
}
}
