css:
/*
	==== MyAnimeList.net - Rounded Corners by Zealotus ====
	Based on code written by Bastvera.
	Tweaked not to interfere with my other styles.
*/
@namespace url("http://www.w3.org/1999/xhtml");
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){*{/*$*/}
	td>div[style="width: 100%; padding-left: 0pt;"]>div {
		border-radius:				3px !important;
	}
	.forum_category {
		border-top-right-radius:	6px !important;
		border-top-left-radius:		6px !important;
	}
	.bgbdrContainer {
		border-radius:				8px !important;
	}
	.quotetext, .codetext, .bgNone, .bgColor1, .bgColor2, #dialog td, .goodresult, .badresult, .inputtext, .textarea, #leftcontent a:hover {
		border-radius:				6px !important;
	}
	#dialog {
		border-radius:				3px !important;
	}
	#horiznav_nav ul li a {
		border-top-right-radius:	3px !important;
		border-top-left-radius:		3px !important;
	}
	#horiznav_nav ul li a:hover {
		border-top-right-radius:	4px !important;
		border-top-left-radius:		4px !important;
	}
	#content {
		border-bottom-right-radius:	8px !important;
		border-bottom-left-radius:	8px !important;
	}
	.inputButton, .button {
		border-radius:				4px !important;
	}
}

@-moz-document regexp("https?://*?myanimelist\.net/about\.php.*") {*{/*$*/}
	.bgColor1, .bgColor2 {
		border-radius:				12px !important;
	}
}
