css:
@-moz-document domain("minnanohimatubushi.2chblog.jp") {
    .blog-title-outer,
    #globalnavi,
    .PR,
    #sub,
    .RSS,
    .RSS1,
    .PR1,
    #PR2-2,
    #kensaku-left,
    #kensaku-right,
    .article-footer,
    .footer-outer,
    #fixfooter,
    .pager,
    #article-options,
    [id^="ldblog_related_articles"]
    {
        display : none !important;
    }

    body {
        background: none;
    }
    #container
    {
        width: 800px !important;
        box-sizing: border-box;
    }

    .column-inner
    {
        width: 780px !important;
        margin-left: 10px !important;
        box-sizing: border-box;
    }

    * {
        font-size: 13px !important;
        font-weight: normal !important;
    }
}
