@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("hromadske.tv") {
     .article-media {
        position: unset;
        width: 100%;
    }

    .article-media iframe {
        height: 590px;
        display: flex;
        left: 3px !important;
        position: absolute;
        float: right;
        width: 100%;
    }
    .page-color1 .add-aside {
        padding-top: 630px;
    }
    
    .page-color1 iframe + .mark-holder {
        content: "&nbsp;";
        position: unset;
        height: 610px;
        display: block;
    }
    
}
