css:
@-moz-document domain(hummingbird.me)
{
    .footer-shadow {
      background-image: url(http://i.imgur.com/5P3yozA.gif);
      width: 100%;
      min-height: 100vh;
      z-index: 3;
      position: relative;
      box-shadow: 0 0 25px 10px rgba(20,20,30,0.2);
    }
  
  	.user-cover .dark-overlay, .group-cover .dark-overlay {
  position: absolute;
  bottom: 0;
  top: 0;
  width: 100%;
  background: #000;
  background: -webkit-linear-gradient(top, rgba(0,0,0,0) 0%, rgba(0,0,0,0.7) 100%);
  background: linear-gradient(to bottom, rgba(0,0,0,0) 0%, rgba(0,0,0,0.7) 100%);
  z-index: 2;
  background-image: url(http://i.imgur.com/5P3yozA.gif);
}
}
