css:
@-moz-document url-prefix("https://gitter.im/") {
  body, #content-wrapper, .chat-header, footer, form, textarea, /*chat-input__box*/.ribbon, .chat-header__avatar, input, .chat-item__container, .chat-item, .popover, .popover-footer, .dropdown, .modal, .modal-footer, .share-view, label, .permissions, div.trpModalBody, div.trpModalSubtext {
    background: #304047 !important;
    color: lightgrey !important;
  }
  
  footer, form, #header-wrapper, .chat-header__avatar {
    background-color: #405057 !important;
  }
  
  a, .search-input, .right-toolbar, .js-chat-topic {
    color: lightgrey !important;
  }
  
  .chat-item__container--status, .gtrCreateRoomButton {
    background: #405057;
    border: none;
  }
  
  .chat-item__text a.link {
    color: #54A8BD !important;
  }
  
  .chat-input__buttons {
    /*filter: invert(100%);*/
  }
  
  .right-toolbar {
    background-color: #405057 !important;
  }
  
  .activity-time {
    color: #999 !important;
  }
  
  .gtrActivityItemText {
    color: #EEE;
  }
  
  .chat-item__icon--read.js-chat-item-readby {
        opacity: .5;
}

}
