body {
  margin-top: 450px;
}

header {
  background-color: #fff;
  margin-bottom: -1px;
  z-index: 99;
}

section {
  background-color: #fff;
  margin-bottom: -1px;
  z-index: 99;
  display: none;/**/
}

.menu-top {
  z-index: 0;
}

footer.full {
  display: none;/**/
}

.dropdown {
  display: block;
  z-index: -99;
}

.dropdown.with-flashsales{
  width: 320px; 
}

.dropdown-right{
    display: none;
}

li.active .dropdown {
    position: fixed;
    top: 0;
    left: 0px;
}

li.home-living .dropdown {
    position: fixed;
    top: 0;
    left: 320px;
}

li.health-beauty .dropdown {
    position: fixed;
    top: 0;
    left: 640px;
}

.dropdown.cf {
    position: fixed;
    top: 0;
