css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {
  div.link-nav-fixed-wrapper {
    margin:20px 0 20px 0;
  }
  div.sidebar {
    background-color:#575757;
  }

  span.beta-tag {
    color:white;
  }
  div.alert {
    display:none;
  }
  div.hero-unit {
    
    display:none;
    visibility: hidden;
    opacity:0;
  }
  div.tagcloud-wrapper {
    margin-top:50px;
    max-height:none;
  }
  div.links {
    margin-top:100px;
  }
  div.ad-container {
    display:none;
    visibility: hidden;
    opacity:0;
  }
  div.link {
    margin:10px 0 10px 0;
  }
  div.item.private {
  background: rgba(200,200,200,0.3);
  }
  .item .domain, .item .user a, .item, .item.private .title,  .item .meta, .item .meta a, item.private .meta {
  color:black;
  }
  .item.private .meta-secondary .actions .action-link {
    color:black;
  }
  .item.private .domain, .item.private .meta, .item.private .meta a, .item.private .user a {
    color:black;
    opacity:0.6;
  }
}
