css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("community.bloodstock.uk.com") {

  #page-body .forumbg .row.bg1 {background-color: #2e2e2e; }
  #page-body .forumbg .row.bg2 {background-color: #222222; }
  #page-body .pagination a {background-color: #151515; padding-top: 3px; }
  #page-body a {color: #ffa016; }
  #page-body form select {background-color: #151515; color: #ddd;}
  #page-body h3 a {text-decoration: none; }
  #page-body h3 { line-height: 1.4ex; }
  #pagebody a { color: #ffa016; }
  #postingbox .inputbox, #message-box textarea { background-color: #2e2e2e; color: #ddd; }
  .post .postbody h3 a { color: #ffa016 !important; font-size: 50%; font-family: sans-serif; font-weight: bold; }
  .post .postbody { width: 78%; float: right; padding-left: 0.6em; }
  .post .postprofile dt { color: #ffa016; }
  .post .signature { font-family: sans-serif; }
  .post blockquote { font-size: 100% !important; margin: 2px; }
  .postbody .content, .post .postprofile dd { font-family: sans-serif; font-weight: normal; font-size: 100% !important; line-height: normal; }
  .postbody blockquote div { background-color: #2e2e2e; border-left: 8px solid #b62a26; padding-left: 0.6em; }
  div.inner {background-color: #1d1d1d; border-bottom: 5px solid #900; padding: 0.6em; }
}
