/*
 * Quora.com - Black by lahratla
 *
 * Author      : lahratla
 * Updated     : June 17, 2015
 * Website     : http://userstyles.org/styles/115309/
 * Description : Modifies the colors of background, borders and texts on quora.com (http://www.quora.com/).
 * Usage       :  To be used with Stylish Firefox extension (https://addons.mozilla.org/en-US/firefox/addon/2108/) or copied in Firefox userContent.css.
 */

@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("quora.com") {

* { color: darkgray !important; background: none !important; background-color: black !important }

a:link { color: teal !important }

a:visited { color: darkslategray !important } 

a:hover { color: lightskyblue !important }

a:active { color: yellowgreen !important }

input[type=button], input[type=submit], input[type=reset]  { background-color: ButtonFace !important; color: ButtonText !important; border-color: ButtonHighlight ButtonShadow ButtonShadow ButtonHighlight !important }

input[type=text], input[type=password], input[type=file] { background-color: Window !important; color: WindowText !important }

}
