css:
@-moz-document regexp("https?://www.comic-rocket.com/(\\?.*)?"), regexp("https?://www.comic-rocket.com/explore/.*") {

h2
{
  font-size: 24px;
}
hr
{
  margin: 2px 0;
}
.MADoc
{
  display: none;
}
.page
{
  margin-top: 50px;
}
.comics-item-readers
{
  display: none;
}

.page .container
{
  width: 1280px;
}

.comics-item
{
  margin: 0;
  position: relative;
  width: 852px;
}

.comics-item-body
{
  width: 572px;
}

.comics-item-progressrow
{
  width: 270px;
  position: absolute;
  top: 0;
  right: 0;
  margin: 0 5px;
}

}
