css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
    domain("adf.ly"), domain("j.gs"), domain("q.gs")
{

/***** ELIMINATED PUBLICATION *****/

#rf {
    display: none !important;
}


/***** ELIMINATED IMAGE *****/

#bottom, #top, #bottomAd, #top span {
    background-image: none !important;
}


/***** COLORS *****/

html, body, td {
    background-color: rgb(16, 16, 16) !important;
}

#sitebar {
    background: -moz-linear-gradient(center top , rgb(24, 24, 24), rgb(0, 0, 0)) repeat scroll 0% 0% transparent !important;
}

#bottom {
    background-color: rgb(8, 8, 8) !important;
    border: 1px solid rgb(0, 0, 0) !important;
    color: #0099FF !important;
}

#bottomAd {
    background-color: transparent !important;
}

#bee {
    border-radius: 0px 0px 16px 0px !important;
}


/***** COLORS 2 *****/

#bottom a, #bottom a:link {
    color: #00BBFF !important;
}
#bottom a:hover {
    color: #0099FF !important;
}
#bottom a:visited {
    color: #0066CC !important;
}
}
