css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("sg.hu") {

  body{
  background-image: linear-gradient(to top, #434343 0%, black 100%);
  }
  
  #menu-family, #menu-family a, .article-block .article-title, .article-block .more, .article-block .comments, .headers-big, .headers-medium, #forum-wrap .forums-block .col1, #forum-wrap .forums-block .col1, #forum-wrap .forums-block .col2, #forum-wrap .forums-block .col3, #forum-wrap .forums-block .col4, #forum-wrap .forum-topics-block > .pull-right li a, #comments-login{    
    color: white;
  }
  
  #content{
    background: #010101;
    color: white;
  }
  
  #header-top, #footer-bottom, #article-body .poll button{
    background-color: #DB0E08;
  }
  
  #menu-heading a:hover, #menu-heading a.active{
    border-bottom: 2px solid #DB0E08;
  color: #DB0E08;
  }
  
  .blue-border-top{
    border-top: 2px solid #DB0E08;
  }
  
  .blue-border-bottom{
    border-bottom: 2px solid #DB0E08;
  }
  
  .advert.bottom-text{
    display: none;
  }
  
  #article-body .poll, #forum-not-logged{
    background-color: #434343;
  }
  
  #article-body a, #forum-posts-list .comment-text a, #messages-block .message a, .post-no, #name, #forum-wrap .forums-block > header .col1{
    color: #DB0E08!important;
  }
  
  #forum-not-logged{
    border: 1px solid white;
  }
  
  #article-info, #breadcrumb{
    color: #bebebe;
  }
  
  #forum-posts-list li{
    background-color: white;
  }
  
  #forum-posts-list li:nth-child(2n){
    background-color: #dddddd
  }
  
  .link-blocks a:hover, .article-block .article-title:hover, .article-block .comments:hover, .article-block .more:hover, #forum-wrap .forums-list > li:hover{
    background-color: #DB0E08;
    text-decoration: none;
    color: #fff;
}
  
  #forum-chat .main-window li:nth-child(2n+1){
    color: #010101;
  }
  
  #forum-chat-list {
    background-coloR: #090909;
}
}
