css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.pptv.com") {
  #container,#dlistcon{width:800px !important;margin-left: auto !important;margin-right: auto !important;}
  
  .tip.tip2,.fastfun{display:none!important;}
  
  #mHeader{position:absolute !important;}
  
  #mHeader,#mHeader > DIV > IMG,#mHeader > UL > LI > DIV > IMG{height: 40px !important;}
  
  #m_player{height: 610px!important; width: 720px !important;}
  
  
  /*简介*/
  .intro.c_b{margin-left: 20px !important;margin-right: 20px !important; width:auto !important;}
  
  .intro.c_b > H3{margin-bottom: 5px !important;font-size: 17px !important;font-family: 新宋体 !important;font-weight: bold !important;}
  
  .intro.c_b > .crumb{font-size:14px !important;font-family: songti !important;color: rgba(255,255,255,0.6) !important;}
  
  .intro.c_b > .introwords{margin-top:5px !important;line-height: 150%!important;font-size:15px !important; font-family: songti !important;color: rgba(255,255,255,0.8) !important;}
  
  /*选集、评论*/
  #infocon{margin-top:0px !important;}
  
  #v_comment_list > DL{margin-left: 100px !important;margin-right: 120px !important; width:auto !important;}
  
  /*推荐*/
  .con.relative{margin-top:0px !important;}
    
  .bd.c_b{margin-left: auto !important;margin-right: auto !important;}
  
  
  HTML > BODY > DIV:nth-child(7) > DIV:first-child > DIV:last-child > DIV:last-child > DL{height: 120px !important; width:91px !important;}
  
  
}
