css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("app.liquidplanner.com") {

.body, #sign_in_user_message, .sessions, .main_content, .rounded_sign_in, .spacer, .inset_content_horizontal>*, .section, .lc, .lhs, .main, .open, .task_list_content, .noprint, .sub_header, .inset, .header_bar, .task_list_header, .task_list_content, tbody, .details, .comment_content, #sub_navigation, .wi_luggage, .inset_content_vertical, .message_content, #home_ribbon, .notification_stream, .timesheet_header, .timesheet_header .timesheet_header_alignment_for_scroll, #timesheet_period_header_2, .timesheet_period_header_with_border, #upcoming_calendar td.day.early, *[class*="sidebar"].light, .help_page, .sidebar_layout {
    background: #222 !important;
    border-color: #bbb !important;
    color: #fafafa !important;
    background-color: #222 !important;
    border-left-color: #222 !important;
}

.nav_group_text, .nav_group_item>a {
    color: #fafafa !important;
}

#upcoming_calendar th.dayname {
    background: #00CCCC !important;
    background-color: #00CCCC !important;
}

.ti, #ut .ut_row_1, .bar.bar_tool {
    background: #111 !important;
    border-color: #bbb !important;
    color: #fafafa !important;
    background-color: #111 !important;
}

.selected {
    background: #4DDBDB !important;
    border-color: #bbb !important;
    color: #111 !important;
    background-color: #00CCCC !important;
}

.nav_group_item:hover a, .nav_group_item.selected a, .nav_group_title.selectable:hover a, .nav_group_title.selectable.selected a {
    color: #111 !important;
}

tr.row, div.comment div.context, div.ti.context{
    background:#222;}

div.comment div.context, div.ti.context, div#notifications div#changed_items div.wi_row div.wi_row_header{
    background:#111;
    background-color: #111 !important;
}

#upcoming_tasks #task_list tr.selected{
    background:#00CCCC !important;}

#upcoming_tasks #task_list tr.row_header td.wi_luggage, #upcoming_tasks #task_list th.wi_start_date{
    background:#111;
    border:#1f67de 1px solid;
    border-left:none;
    border-right:none;
    border-bottom:none;
    
}

#treeitem_panel .header{
    box-shadow:none;}

#upcoming_tasks div.task_list_header{
box-shadow:none;
}

::-webkit-scrollbar {
    background-color: #555;
    width: 1em;
}
 
::-webkit-scrollbar-thumb {
  background-color: #0060ff;
}
}
