@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("opennet.ru") {
  td.ftxt, .ctxt, .tlist, .chtext, BLOCKQUOTE,
  HTML > BODY > CENTER > table tbody tr TD > FONT,
  HTML > BODY > CENTER > P > table tbody tr TD > FONT,
  HTML > BODY > CENTER > P > table tbody tr TD > BLOCKQUOTE > FONT
 {
     font-size: 100% !important;
  }
  
    table.ttxt tbody tr td table tbody tr td table tbody tr td table.tlist
 {
     font-size: 100% !important;
  }
  a:visited { color: #636 !important; }
  
  noindex,
  body > div,
  #adv,
  #adv1,
  #adv2,
  #adv3,
  #adv4,
  .tbrd,
  html body center table[width="50%"],
  html body table tbody tr td table tbody tr td center,
  html body form br,
  html body table tbody tr td ins.adsbygoogle,
  form > table > tbody > tr > td:nth-of-type(n+3),
  #social_link,
  body > br:nth-of-type(n+1)
  {
    display: none !important;
  }
 center > table > tbody > tr > td {
    font-size: 0.9em !important;
  }
  
  body table tbody tr td table tbody tr td table.thdr:nth-of-type(-n+1){
    margin-top: 0px !important;
  }

  center > table {
   width: 81% !important;
    max-width: 81% !important;
    border: 0 !important;
    position: relative !important;
  }
  body > center {
    width: 99% !important;
  }
}
