css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("tv.so-net.ne.jp") { 
    input[id="condition_keyword"] {
        width: 600px !important
    }
}
@-moz-document url-prefix("http://tv.so-net.ne.jp/m/chart.action") { 
    /* 非表示 */
    .pRcolumn,
    div.contBlockST:nth-child(3),
    .contBlockB1,
    #interest_match,
    .bottomContent,
    div.contBlockNB > iframe,
    .footer,
    #sponsoredSearch {
        display: none
    }
    /* 番組表より上部 */
    input[id="condition_keyword"] {
        width: 600px !important
    }
    .topicPath {
        width: 725px;
        margin-top: -20px;
        padding-right: 225px;
    }
    #topColumn > div:nth-child(1) {
        width: 1200px;
        margin-left: -125px;
    }
    ul.timeLineList:nth-child(2) {
        margin-left: 560px;
        margin-top: -40px;
    }
    div.contBlock:nth-child(9) {
        width: 638px;
        margin-left: 560px;
    }
    .chartTitle {
        font-family: "impact";
        text-shadow: 3px 3px 1px #999999;
        font-size: 30px;
    }
    /* 番組表より下部 */
    .lastUpdate {
        position: absolute;
        top: 230px;
        left: 250px;
        color: red;
        font-size: 16px;
    }
    #simpleChartFooter {
        width: 1200px
    }
    ul.timeLineList:nth-child(1) {
        margin-left: 558px
    }
    .dateList {
        width: 640px;
        margin-left: 559px;
    }
    div.contBlockST:nth-child(4) {
        width: 550px;
        height: 50px;
        overflow: auto;
        margin-top: -55px;
    }
}
