css:
@-moz-document domain("engadget.com") {
#nav-main>ul, #search-container, #redbar, #carousel, #rail, #footer,
#header-social-icons, .default-dynamic-lede, .newsletter, .border, #hottopics-container, #mobileaddiv, .sponsor-logo-post, #sub-frame-error, .gallery-post-4, .leftbanner, #reviews-container, #hot-topics, #header-post-nav
{ display: none !important; }

body {
  background: #454545 !important;
}
#page article.post {
    box-shadow: 0 1px 12px 1px rgba(0, 0, 0, 0.6) !important;
}


#page, .header {
  width: auto;
  max-width: 730px;
}
#page #body {
  float: none;
  width: auto;
  margin: 0 auto;
}

.home:not(.uk-home) .post h2 a {
    font-size: 34px;
}
.h1, #page .post header .h2 {
  font-size: 24px !important;
}
#page article.post {
  width: 670px;
  margin: 0 auto 40px;
  box-sizing: border-box;
  background: #fff;
  padding: 20px 40px;
  box-shadow: 0 1px 2px 1px #eee;
}

 .home:not(.uk-home) .post p {
width: 610px;
          }

.post .post-body {
  border: 0;
}
.perm .post-body {
  max-width: 710px !important;
  margin: 0 2px;
  padding: 0 40px 15px !important;
  box-shadow: 0 1px 2px 1px #eee;
}
}
