css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("silicon.fr") {
  
  /*nettoyage*/
  .wrap-article-info, .newsletter, .follow, #div-gpt-ad-DESKTOP_MEGA-1, #div-gpt-ad-DESKTOP_MEGA-2, .grid-336, .after-article-left,
  #footer, .toaster, .post-navigation, .entry-register-newsletter, .entry-breadcrumb, .entry-header > .entry-tools, .search-button,
  #hot-topics, .event, .dossier-widget, .archive article.sponsored {
    display: none !important;
  }
  
  .wrap-article-content {
    width: inherit !important;
  }

  header > #primary-bar > .wrapper, #main {
    width: 95% !important;
  }
  
  .grid-684, .single article .wrap-article-content figure img, .page article .wrap-article-content figure img, .wmvid, .single-blog article.blogpost {
    width: 100% !important;
  }
   
  .entry-title, #main {
    margin-top: 1% !important;
  }
  
  .single .author-box .author-summary, .page .author-box .author-summary {
    width: 90%;
  }
    
  .single article .wrap-article-content figure img, .page article .wrap-article-content figure img {
    max-width: 35%
  }
  
  #page .wrapper #middle_wrap #middle {
    width: 100%;
  }
  
    /*dark color*/
  #page, #primary-bar, section, body, .single #content, .page #content, #page .wrapper #middle_wrap, #page .wrapper #middle_wrap .articleList .article:nth-child(2n+1) {
    background-color: #333 !important;
  }
  
  .single article header.entry-header, .page article header.entry-header {
    margin-bottom: 0px !important;
    padding: 1% !important;
  }
  
  header, .entry-header > .entry-title > a, .entry-header > .entry-content {
    color: #6C7A89;
    background-color: #333;
  }
  
  .archive-header {
    color: #BDC3C7;
  }
  
    .entry-header > .entry-title > a:hover {
    color: #6B6990;
  }
  
  p, .pf-content > h2, .single article .wrap-article-content .article-content .entry-content,
  .page article .wrap-article-content .article-content .entry-content,
  .single article header.entry-header .entry-title, .page article header.entry-header .entry-title {
    color: #BDC3C7 !important;
  }
  
    .entry-title a {
    color: #7E7BB7 !important;
  }
  
  section > footer > span > a {
    color: #7E7BB7 !important;
  }
  
  section > footer > span > a:hover {
    text-decoration: underline !important;
    color: #6ad3ff !important;
    transition: color 0.2s linear 0s;
  }
  
  .entry-title a:hover {
    text-decoration: underline !important;
    color: #6ad3ff !important;
    transition: color 0.2s linear 0s;
  }
  
  .single article header.entry-header .entry-author-date a {
    color: #7E7BB7;
  }
  
   .single article header.entry-header .entry-author-date a:hover {
    text-decoration: underline !important;
    color: #6ad3ff !important;
    transition: color 0.2s linear 0s;
  }
  
  .single article header.entry-header .entry-author-date a:hover, .page article header.entry-header .entry-author-date a:hover {
    text-decoration: underline !important;
    color: #6ad3ff !important;
    transition: color 0.2s linear 0s;
  }

 
  
  .single article .wrap-article-content .article-content .entry-content a:hover, .page article .wrap-article-content .article-content .entry-content a:hover {
    text-decoration: underline !important;
    color: #6ad3ff !important;
    transition: color 0.2s linear 0s;
  }
   
}
