@-moz-document domain("manga-joy.com") {
#wrapper,#wrapper-content,#header,table,#sct_reader_page .wrap{width:100%;}
#logo{margin:0;float:none!important;}
#fb-root,#scrollBottom,
  #sidebar,
  .topmenu-area,
  #sct_reader_footer,
  #sct_reader_banner_bottom,
  .wpm_tip,
  .wpm_ifo_box, 
  script, 
  .clearspace-bottom,
  .textwidget,
  #sct_footer_banner,
  #sct_reader_top,
