css:
@-moz-document url-prefix("http://s.maho.jp/homepage/e63db7da004dd0bb/") {
#mahoimain {
    text-align: center;
    font-size: 22px;
    font-family: "MS Mincho";
}

#mahoimain br {
    text-align: center;
    font-size: 10px;
}

#mahoimain > a,
div[align='right'] a {
    margin-left: auto;
    margin-right: auto;
    display: block;
    color: #fff;
    background-color: #e60012;
    text-align: center;
    text-decoration: none;
    text-transform: uppercase;
    border: 1px solid #fff;
    padding: 6px 34px 6px 40px;
    font-size: 24px;
    font-family: monospaced;
    font-weight: bold;
    width: auto;
    max-width:150px;
}

#mahoimain > a:hover,
#mahoimain > a:focus,
div[align='right'] > a:hover,
div[align='right'] > a:focus {
    color: #e60012;
    background-color: #fff;
    border: 1px solid #e60012;
}

div[align='right'] {
	 position: fixed;
    right: 35px;
    bottom: 40px;
}

#mahoimain img[src='/share/image_archive/00/c4/00c488d5216a1868/c0/c0edc36320.gif'] {
    width: 26%;
}

div[style][align]{
	display: none;
}
}
