.CodeMirror {
  background: rgb(229, 229, 229);
    font-size: 14px;
}

.CodeMirror-gutters {
    background-color: #D0D0D0;
}

#header {
  border-right: 1px solid #121212;
}    
button {
  outline: none;
  color: #aaa;
  fill: #aaa;
  border: 1px solid #222;
  border-radius: 2px;
  font-family: 'Roboto', sans-serif;
  font-size: 12pt;
  line-height: 18px;
  height: 24px;
  cursor: pointer;
  padding: 2px 6px;
  transition: 100ms;
  min-width: 75px;
  background-color: #444;
  background-image: linear-gradient(to bottom, #444, #333);
}

}
