@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("forum.jogos.uol.com.br") {
  
  body {
   background-color: #484848 !important;
 }
  
  #content-inside, div#social-div, .boxGotoContent, div#importTab1, .star, .posts-container, div#previewContent, .topicRow, div#importTab1 dd .icon {
    background-color: #c4c2c2 !important;
  }
  
  input#search-field, input, button, textarea, select, optgroup, option, div#estatisticas .texto {
    background-color: #f2f2f2 !important;
  }
  
  .postRow {
    background-color: #f2f2f2
  }
  
  .quote {
    background-color: #EAEAEA !important;
  }
  
  .search-on-forum {
    color: #474747 !important;
  }
  
  div#publicidade {
    display:none;
  }
  
  .row1 {
    background-color: #e7e7ed !important;
  }
  
  div#legendas {
    background-color: #c4c2c2 !important;
  }

  #station-logo {
  text-shadow:1px 1px 0 #c4c2c2,
-1px -1px 0 #c4c2c2,
1px -1px 0 #c4c2c2,
-1px 1px 0 #c4c2c2,
0px 1px 0 #c4c2c2,
1px 0px 0 #c4c2c2,
0px -1px 0 #c4c2c2,
-1px 0px 0 #c4c2c2,
4px 4px 3px #000;
  }
  
/* ------------------Responder-------------------- */  
  .formSend, .divSmilesPag {
    background-color: #474848;
    color: #e3e3e3 !important;
  }
  
  #contador-messageContent, .formSend a, .smiles-pagging, .title, .uolmais-buttons > a {
    color: #f2f2f2 !important;
  }
  
  .formSend-field-noborder b, .formSend-field b, .formSend-video b {
    color: #adc2f1;
  }
  
  .divSmilesPag a {
    color: #46bed9;
  }
  
}
