css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slovari.yandex.ru") {
  
  body {
    background: black;
  }
  
  .b-form-input__box {
    background: gray;
  }
  
  h2, .b-translation__title, .b-translation__title .b-translation__comment, .b-translation__title .b-translation__abbr, .b-translation__tr, .b-translation__example-original, .b-translation-parts__link, .b-translation-themes__link ajax ajax-full, .b-translation-themes__link, .b-translation-see-also__original, .b-translation-review_theme_new .b-translation-review__link {
    color: lightgray;
  }
  
  .b-translation__title .b-translation__text, .b-translation__title .b-translation__comment, .b-translation__title .b-translation__abbr {
    color: lightgray;
  }
  
  .b-translation__example-original .b-translation__text {
    color: lightgray;
  }
  
  .b-translation__text {
    color: lightgray;
  }
  
  .b-translation__translation-words, .b-translation__src-topnum, .b-translation-parts__header, .b-translation-themes__header, .b-translation-see-also__header, .b-translation-review_theme_new .b-translation-review__header, .b-head-search {
    background-color: black;
  }
  
  .b-translation__link, .b-link_pseudo_yes {
    color: lightblue;
  }
  
  .l-head__l, .b-lingvo-logo, .b-rtb, .b-head-tabs, .b-head-search__arr-i, .b-link_fulltext_yes, .b-foot, .b-user {
    display: none;
  }
  
  .b-translation__group, .b-translation__rejex, .b-translation__cols, .b-form-input_theme_grey .b-form-input__box, .b-translation-parts, .b-translation-themes, .b-translation-see-also, .b-translation-review_theme_new, .b-form-input_theme_grey.b-form-input_focused_yes .b-form-input__box {
    border: 0;
    box-shadow: none;
  }
    
}
