css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document
  url-prefix("http://www.pixiv.net/novel/"), 
  url-prefix("http://www.pixiv.net/series.php?") {
  ._novel-item .novel-contents {
    position: relative;
    min-height: 155px !important;}
  .list-b ._ui-tooltip.bookmark-box {
    position: absolute;
    z-index: 5;
    width: 63px !important;}
  .list-b ._ui-tooltip.bookmark-box:nth-of-type(1):not(:last-of-type) {
    top: 125px;
    left: -3.5px;}
  .list-b ._ui-tooltip.bookmark-box:last-of-type:first-of-type {
    top: 0px;
    left: 3px;}
  .list-b ._ui-tooltip.bookmark-box:nth-of-type(2) {
    top: 149px;
    left: -3px;}
}
