css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kaldi.sourceforge.net") {
	body {
		background-color: #000;
		color: #EEEEEE;
		font-family: "Source Sans Pro", Helvetica, Arial, sans-serif;
	}
	
	title {
		color: #333333;	
	}
	
	#nav-tree {
		background-color: #222200;
		color: #EEEEEE;
	}
	
	a, a.code, a.el {
		color: #99CCFF;
	}

	.contents a:visited {
		color: #66CCEE;
	}
	
	span.comment {
		color: #AA6600;
	}
	
	.memdoc {
		background-color: #000000;		
	}
	
	.title {
		color: #331100;
	}
	
	div.fragment, pre.fragment {
		font-family: "Envy Code R", Consolas, "Lucida Console", Monaco, monospace;
		font-size: 0.9em;
		background-color: #1F1F1F;
		border-color: #003300;
	}
	
	span.stringliteral {
		color: mistyrose;
	}
	
	span.keyword {
		color: yellowgreen;
	}

	.mdescLeft, .mdescRight, .memItemLeft, .memItemRight, .memTemplItemLeft, .memTemplItemRight, .memTemplParams {
		background-color: #111111;
		border-color: #777777;
		font-family: "Envy Code R", Consolas, "Lucida Console", Monaco, monospace;
		font-size: 0.9em; 
	}

}
