css:
@-moz-document domain("couchcms.com") {

* {
    font-size: 1rem;
}


#logo {
     width: 100% !important;
     margin: 0 1em !important; 
}

#wrap, 
#nav,
#nav2,
#nav_back,
.catmain, 
#hoverpage,
#hoverpage ul#menu li.page div {
  width: 100% !important;
  padding: 0 !important;
}

td.row1,
td.row2 {
    padding: 0;
}


p,
p.forumdesc,
span,
div,
table {
    word-break: normal !important;
    white-space: normal !important;
    word-wrap: normal !important;
}


p.forumdesc {
  color: #B3B3B3;
  font-size: 0.7em;
  font-family: sans-serif;
}

p.topicdetails {
  font-size: 0.8em;
}


