#wrapcentre > table:nth-child(6) > tbody > tr:nth-child(2) > td:nth-child(1) {
    width: 5% !important;
}

#pagecontent > table.tablebg > tbody > tr > td:nth-child(3),
#pagecontent > table.tablebg > tbody > tr > td:nth-child(2) {
    width: 50% !important;
}


#pagecontent > table.tablebg > tbody > tr:nth-child(6) > td:nth-child(3) {
    width: 6% !important;
}

#pagecontent > table.tablebg > tbody > tr:nth-child(4) > td:nth-child(6) > p:nth-child(2) > a:nth-child(1) {
   word-break: break-word;
  word-wrap: initial;
  white-space: inherit;

}



a:hover {
  color: #0F0F0F;
  text-decoration: underline;
}

body,
.tablebg,
#innerwrap {
  background: none; 
}

.genmed,
.postdetails,
.gensmall,
p.datetime {
 font-size: 0.8em;
    color: #A9A9A9;
}

#inner_bdr,
#innerwrap {
    border: none;
}


td.profile {
  width: 5% !important;
}

#pagecontent > table > tbody > tr > td > div > a > img {
    width: 110px;
}

}
