css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("getpocket.com") {
body {
    background-color: #F3F2EE !important;
}
.toolbar_queue_divider,.toolbar_reader_divider{
    display: none;
}
.pkt-nav {
    background-color: #F3F2EE !important;
    border:none !important;
}
.item_content {
    border:none !important;
}
.reader_content {
    max-width: 50em !important;
}
.text_body blockquote {
    border-color: #BABABA;
    color: #313131;
    border-left-width: 3px;
    margin: 1.5em 0px;
    padding: 0em 1em;
}
.reader_head h1 {
    font-size: 1.6em;
}
}
