css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bing.com") {
  body {
     font-family: Arial, sans-serif;
 }
  
  h2, h3, .b_no h1 {
    font-size: 18px;
  }
  
  h1 strong, h2 strong, h3 strong, .b_xlText strong, .b_focusTextSmall strong {
    font-weight: 600;
  }
  
  .b_ad {
    font-size: 12px;
  }
  
  .b_scopebar ul {
    padding: 0px 0px 0px 157px;
  }
  
  #b_content {
    padding: 0px 0px 20px 150px;
  }
  
  #b_results {
    width: 700px;
  }
  
  #b_results > li a  {
    color: #1A0DAB;
  }
 
  #b_context {
    float: right;
  }
  
  #b_header #id_h {
    position: fixed;
    top: 0;
    right: 0;
    margin: 10px 0 0 0;
  }
  
  cite, #b_results cite.sb_crmb a {
    color: #006621;
  }
}
