css:
/* i really want this to be global */

.lowerContentRow {        
    top: 0px;
}

.adsNavContainerDiv {
    display: none;
}
    
.action-bar {
    display: none;
}

div.canvas-column.col-sm-12.ng-isolate-scope.ui-draggable.ui-droppable.adCanvasNoProperties  {
    height: 100% !important;    
}

#mainView {
  	height: 100% !important;    
  	overflow: visible !important;    
}

#appContainer > div > div.lowerContentRow > div.rightContentDiv > ad-view-stack > div {
	height: 100% !important;    
    overflow: visible !important;    
}

#appContainer > div > div.lowerContentRow > div.rightContentDiv > ad-view-stack > div > div.container.ng-scope {
    height: 100% !important;    
    overflow: visible !important;    
}


/***** HACK UPDATE FOR APP DYN 4.1 ******/
#topMenuBarContainerDivId, #mainTitleBarContainerId {
    display: none;
}

.ads-main-container-under-top-menu-bar {
	top: -52px;
}

#appContainer > div > div.ads-main-container-under-top-menu-bar > div.ads-right-side-content-container > div.ads-right-side-under-location-bar-container > ad-view-stack > div > div.container.ng-scope {
    height: 100% !important;
    overflow: visible !important;    
}

/***** HACK UPDATE FOR APP DYN 4.2 ******/
.custom-dashboard-container-2 .ads-border-top .ng-scope .absolute-canvas {
	    display: none;
    
}

ad-main-container {
    top: 0px !important;
    z-index: 10;
    overflow: visible !important;
}

.ads-main-container-scroll-pane {
    overflow: visible !important;
}
