css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("55ch.org"), domain("8ch.net"), domain("boards.4chan.org") {
  
  /*8chan and other tinyboard based IB's*/
  .post-image{
    opacity: 0.05;
  }

  .post-image:hover{
    opacity: 1;
  }
  
  /*4chan*/
  div.post div.file .fileThumb img{
    opacity: 0.05;
  }

  div.post div.file .fileThumb img:hover {
    opacity: 1;
  }
}
