/* Slicer Dark Mode Version .02 By gnarvin*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slicer.io") {
  

  body, .secondarybar, .scenecontainer, .scenecontent, .gray.hovergreen.pull-right, .container.normal-container , .boxcontainer, .eight.columns div, .outside-container nav, .pbackbtn,
  .pnextbtn, .search-button, .ten.columns.offset-by-one.page-container, .hiddeninlines div 
  {
    background-color: #333 !important;
    color: white !important;
    border-color: #222 !important;
  }

  a:link, .navtitle {
   color: white;
  }
  a:visited { 
    color: gray;
  }
  a:hover {
    color: slateblue !important;
  }

  .eight.columns div div textarea, .hiddeninlinetab input{
    background: #555;
    color: #222;
    border-color: #222 !important;
  }
  
  .button.button-primary, .button-primary{
    background-color: slateblue !important;
    border-color: slateblue !important;
  }
  
  .button.button-primary:hover, .button-primary:hover{
    background-color: darkslateblue !important;
    border-color: darkslateblue !important;    
  }
  
}
