/* Slicer Dark Mode Version 1.2 By gnarvin*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slicer.io") {
  
  /* ~~~~~~~~~~~~~~~~~~   Main Dark ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  body, .secondarybar, .scenecontainer, .scenecontent, .gray.hovergreen.pull-right, .container.normal-container , .boxcontainer, .eight.columns div, .outside-container nav, .pbackbtn,
  .pnextbtn, .search-button, .ten.columns.offset-by-one.page-container, .hiddeninlines div, .button.icon-info-circled.mr-5, .scenecontent div div a, .dropdownlink.icon-globe, 
  .icon-mail-alt.dropdownlink, .dropdownlink, .twelve.columns.page-container, .icon-comment, .icon-up-open, .icon-edit, .icon-trash-empty, .svgcontainer, .button, 
  .sharing-container select
  {
    background-color: #333 !important;
    color: white !important;
    border-color: #222 !important;
  }
  
  /* ~~~~~~~~~~~~~~~~~~   Links ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  a:link, .navtitle {
   color: white;
  }
  
  /* ~~~~~~~~~~~~~~~~~~   Visited ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  a:visited { 
    color: gray;
  }
  /* ~~~~~~~~~~~~~~~~~~   Hover ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  a:hover, .icon-comment:hover, .icon-up-open:hover, .icon-edit:hover, .icon-trash-empty:hover, .pbackbtn:hover, .pnextbtn:hover, .dropdownlink.icon-globe:hover, 
  .icon-mail-alt.dropdownlink:hover, .dropdownlink:hover, .button:hover, .sharing-container select:hover
  {
    color: slateblue !important;
  }

  /* ~~~~~~~~~~~~~~~~~~   Text Aeras ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  .eight.columns div div textarea, .hiddeninlinetab input, .svgcontainer div input{
    background: #555;
    color: #222;
    border-color: #222 !important;
  }
  
  /* ~~~~~~~~~~~~~~~~~~   Buttons ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  .button.button-primary, .button-primary{
    background-color: slateblue !important;
    border-color: slateblue !important;
  }
  
  /* ~~~~~~~~~~~~~~~~~~   Buttons Hover ~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  .button.button-primary:hover, .button-primary:hover{
    background-color: darkslateblue !important;
    border-color: darkslateblue !important;    
  }
  
  /* ~~~~~~~~~~~~~~~~~~   Upvote and downvote Arrows~~~~~~~~~~~~~~~~~~~~~~~~~~~~~~*/
  .icon-up-big.upvote.upvoted, .icon-up-big.upvote:hover{
    color:#FF4500 !important;;
    
  }
  .icon-down-big.downvote.downvoted, .icon-down-big.downvote:hover{
    color:#00F !important;
    
  }
  
}
