css:
@-moz-document domain("dfiles.ru"), domain("depositfiles.com") {
.choose_download.hide_download_started > tbody > tr > td:first-child, .service18, [type="application/x-shockwave-flash"], .banner1[style="text-align:center;"], [id^="ads_z"], #banner_place, #foobar, #main > .top_menu, #member_menu, .subscribe_offer.subscribe_offer_banner, .violation, .gold_speed_promo_block, .support, .report, .downloadblock > script:first-child + .string, .chousetype + .choose, .chousetype > tbody > tr img, .chousetype #wait, iframe[src="http://dfiles.ru/payment_bornpay_subscribe.php"], #Networkdownloader2, .choose, .downloadblock.downloadblock_limit .ctable1right, .page_download_limit #iframe_console {
    display: none;
}

#iframe_console {
    top: 0 !important;
    left: 0 !important;
    right: 0 !important;
    bottom: 0 !important;
    border: none;
    padding-top: calc(50vh - 20px);
    padding-left: calc(50vw - 100px);
    font-size: 20px;
}

#iframe_console table:before {
    content: "Press this: ";
    margin-right: 10px;
}

#iframe_console table, #iframe_console tr, #iframe_console td, #iframe_console tbody {
    display: inline-block;
    vertical-align: -2px;
}

.chousetype > tbody > tr > td:first-child {
    opacity: 0.3;
}

.chousetype .download_url a {
    margin-top: 20px;
}

.downloadblock .info {
    border-bottom: none;
}

.carcasstable1 .ctable1left {
    border-right: none;
}
}
