css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.google.com/calendar/"), 
               url-prefix("https://www.google.com/calendar/") {

  /* column 7 = Saturday */

  #decowner > [style*="left: 7"] > div:not(.currentDayDec) {
    background-color: #D1E6B3 !important; /* light green */
  }

  /* column 8 = Sunday */

  #decowner > [style*="left: 8"] > div:not(.currentDayDec) {
    background-color: #D1E6B3 !important; /* light green */
  }

  /* Current day */

  #decowner > div > .currentDay {
    background-color: #FFFF99 !important; /* bright yellow date heading */
  }

  /* Mini month view */

  .DP_weekend {
    background-color: #D1E6B3 !important;
  }

  .DP_weekend_selected {
    background-color: #B1C693 !important;
  }

}
