css:
@-moz-document url-prefix('http://quickposes.com/') {
  
  /* changes background color to a very dark grey. */
body {
  background: #111;
  margin-bottom: -50px;
}

  /* changes font color in green note to black instead of white. */  
.label-green, .label-success {
  color: #000;
  
}

  /* changes button color to black instead of white. */  
.ui-state-default, .ui-widget-content .ui-state-default, .ui-widget-header .ui-state-default {
  background: #000;
}

  /* adds a custom highlight to the buttons when hovering over them. */
.ui-state-default:hover, .ui-widget-content .ui-state-default:hover, .ui-widget-header .ui-state-default:hover {
  background: #333;
  color: orange;
}
  
  /* changes the pixels at bottom of page to the same color as background. */  
.page-challenges {
  background: #111!important;
}
  
}
