css:
@-moz-document url-prefix("http://ppy.sh/") {
body {
    background: url(https://tribwgno.files.wordpress.com/2014/12/176738392.jpg);
    background-size: 100% 100%;
    background-repeat: no-repeat;
}

.about_background {
    background: #222;
}
}

@-moz-document domain("blog.ppy.sh") {
body {
    background: #222;
    color: #dead00;
}
strong {
    color: #0dead0;
}

#container {
    background: transparent;
}

h3.title {
    border-bottom-color: #BF0050;
}

h3.title a {
    color: #5000FF;
}

#header-container {
    border-bottom-color: #FF0050
}

.copy a {
    background: inherit;
}

code {
  background-color: #343434;
  color: #BBB;
}
}
